import  { useState,useEffect } from 'react';
import React from "react";
import './itemNew.css';

import {
  BrowserRouter as Router,    
  Link,useParams
} from "react-router-dom";



function Login() {



    useEffect(()=>{
     //
     let isLoggedIn = localStorage.getItem('isLoggedIn');
     alert(isLoggedIn);
    checkLoggedUser();

    },[]);

    //control if user is already logged in
    function checkLoggedUser(){
        let isLoggedIn = localStorage.getItem('isLoggedIn');
        if(isLoggedIn=='true'){
            window.location.href = "/welcome";
            alert('a');
        }        
    }


    function submitLogin(){
        localStorage.setItem('isLoggedIn','true');
    }

    return (


        <main className='container top2'>
            <div className='row left'>
                <h5 className='col left'>Sign in</h5>
            </div>

            <label className='row top1 lightgrey'>Email    
                    <input type='text'></input>            
            </label>

            <label className='row top1 lightgrey'>Password    
                    <input type='text'></input>            
            </label>

            <div className='row top1'>
                <button className='btn btn-primary' onClick={()=>{submitLogin()}}  >ok</button>
            </div>

            <div className='row top1'>
                <button className='btn green'>Sign up</button>
            </div>

            <div className='row top1'>
                <button className='btn green' onClick={()=>{localStorage.clear()}}>clear</button>
            </div>






        </main>    
    );
  }

  export default Login;
