import  { useState,useEffect } from 'react';
import React from "react";
import './signUp.css';

import {
  BrowserRouter as Router,    
  Link,useParams
} from "react-router-dom";



function SignUp() {



    useEffect(()=>{
     //

    },[]);

    const [username, setusername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setpassword] = useState('');
    const [repeatpassword, setrepeatpassword] = useState('');



    //control if user is already logged in
    function checkLoggedUser(){
        let isLoggedIn = localStorage.getItem('isLoggedIn');
        if(isLoggedIn=='true'){


            let email=localStorage.getItem('email');


            // window.location.href = "/welcome";

        }     


    }


    function submitLogin(){
        localStorage.setItem('isLoggedIn','true');
    }

    return (


        <main className='container top2'>

            <div className='row maincontent'>
                <Link to='/' className='col-12'><i class="fas fa-caret-left"></i></Link>
            </div>

            <div className='row top1'> 
                <h5 className='col-12 green'><b> Sign up </b></h5>    
            </div>
            <label className='row top1 grey'>Username 
                <input type='text'></input>
            </label>            

            <label className='row top1 grey'>Email
                <input type='text'></input>
            </label>            

            <label className='row top1 grey'>Password
                <input type='text' id='password' onKeyUp={()=>{
                    setpassword(document.getElementById('password').value.trim());
                }} ></input>
            </label>        

            {
                password!='' &&
                <label className='row top1 grey'>Repeat Password
                <input type='text' id='repeatpassword'></input>
            </label> 


            }



            <div className='row top1'>
                <button className='btn btn-primary'>Sign up</button>
            </div>







        </main>    
    );
  }

  export default SignUp;
