import  { useState,useEffect } from 'react';
import React from "react";
import {
    BrowserRouter as Router,    
    Link,useParams
  } from "react-router-dom";


import './Stock.css';

function StockCategory() {

    let category = 'Fruits and Vegetables';
    let { id } = useParams();
    return (        
      <main className='container top1'>

          <div className='row'>
              <Link to='/stock' className='col-12'><i class="fas fa-caret-left"></i></Link>
          </div>

          <div className='row top1'>
            <div className='col-6 green vertical-centre'>
                <b>{id} {category} </b>
            </div>

            <div className='col-6 '>
                <button className='black'><b> + Add item</b></button>
            </div>

          </div>

          <div className='row top1'>
              <div className='col  centre'><small className='grey'>No items</small> </div>
          </div>



          <div className='row top1'>
              <div className='col-8  record vertical-centre'>
                  Apples
              </div>
              <button className='col-1 left1 centre button-secondary'>+/-</button>
              <button className='col-1 left1 centre button-secondary'><i class="fas fa-cog"></i></button>
              <button className='col-1 left1 centre button-secondary'><i class="fas fa-trash-alt"></i></button>
          </div>

          <div className='row top1'>
              <div className='col-8  record vertical-centre'>
                  Oranges
              </div>
              <button className='col-1 left1 centre button-secondary'>+/-</button>
              <button className='col-1 left1 centre button-secondary'><i class="fas fa-cog"></i></button>
              <button className='col-1 left1 centre button-secondary'><i class="fas fa-trash-alt"></i></button>
          </div>




      </main>            
    );
  }



  export default StockCategory;
