// import './main.css';

import  { useState,useEffect } from 'react';
import React from "react";
import './itemNew.css';

import {
  BrowserRouter as Router,    
  Link,useParams
} from "react-router-dom";


// import household from './images/stock/household.png'




function ItemNew() {

    let { category } = useParams();

//   const [selected_Stock, select_Stock] = useState(0);  









//   useEffect(() => {
//     let commodityType=document.getElementsByClassName('commodityType');
//     let l=commodityType.length;
//     let c;
//     for(c=0;c<l;c++){
//       commodityType[c].style.color='black';
//     }
//     commodityType[selected_Stock].style.color='lightseagreen';
//     });


  return (        
    <main className='container top1'>

        <div className='row'>
              <Link to={'/stock-category/'+category} className='col-12'><i class="fas fa-caret-left"></i></Link>              
        </div>

        <div className='row top1'>
            <div className='col green'><b>New item</b><span className='lightgrey'> |{category}</span></div>
        </div>

        <form className='row top1 border'>

            <div className='col-12 border'>
                <label className='col-8 grey'>Name
                    <input type='text' className='col-12'></input>                    
                </label>
                <button className='left1'><i class="fas fa-info-circle lightgrey button-img"></i></button>

                <label className='col-8'>Name
                    <input type='text' className='col-12'></input>                    
                </label>
                <button className='left1'><i class="fas fa-info-circle lightgrey button-img"></i></button>
            </div>








        </form>

        <div className='row top1'>
        <button className='btn btn-warning border col-2 green'><i class="fas fa-angle-left button-img"></i></button>
        <button className='border col-2 left2 green'><i class="fas fa-angle-right button-img"></i></button>
        <button className='border col-2 left2 green'><b>Done</b></button>
        </div> 


    </main>            
  );
}

export default ItemNew;
