import './welcome.css';


import React from "react";

import  { useState,useEffect } from 'react';

import {Link} from "react-router-dom";



function Welcome() {


  localStorage.clear();
  const [all_Welcome, getAll_Welcome] = useState('');
  const [selectedHousehold, setSelectedHousehold]=useState('');


  useEffect(()=>{
    getHouseholds();

  },[]);


  function select_Welcome(household){
    localStorage.setItem('household',household);
  }

  function getHouseholds(){

    var xhttp = new XMLHttpRequest();

    xhttp.open("GET", "http://localhost:8080/api/getHouseholds");
    xhttp.send();

    xhttp.onload = function() {
      if(this.status==200){
          let response=JSON.parse (this.responseText);
          getAll_Welcome(response.folders);  

          if(response.length==3){
            document.getElementById('addHouseHold_Welcome').style.display='none';

          }
          else{
            document.getElementById('addHouseHold_Welcome').style.display='block';
          }
          // document.getElementById('addHouseHold_Welcome').style.display='block';
      }
  };

  }  



  function showItems_Welcome(){

      let households=[];

      let l = all_Welcome.length;
      let c;

      for(c=0;c<l;c++){

        if(all_Welcome[c]!='.DS_Store'){
            let n=c;
            households.push(<div className= 'row border top1  centre vertical-centre'>
                  <Link to={'/transactions'}>

                       <button className='col-12' onClick={()=>{select_Welcome(all_Welcome[n])}} >
                          <i class="fas fa-house-user button-img green"></i> 
                       </button>                                            
                        <span className='col-12 black'>{all_Welcome[c]}</span>                                                            
                  </Link>
                  <button className='col' onClick={()=>{

                    openModal(all_Welcome[n]);
                  }}><i class="far fa-edit"></i></button>
                </div>
            );

        }

      }

      return (<div>{households}</div>);


  }


  function openModal(household){

    //set selected household
    setSelectedHousehold(household);


    //set the household's name in the modal's input
    document.getElementById('input_household').value=household;


    let modal=document.getElementsByClassName('modals2');

      modal=modal[0];
      modal.style.display='block';
      setTimeout(() => {
        modal.style.transform='translateY(0)';
        modal.style.opacity='1';



      }, 100);
  }


  function closeModal(){  

    let modal=document.getElementsByClassName('modals2');
    modal=modal[0];

    modal.style.transform='translateY(-10px)';
    modal.style.opacity='0';          
    setTimeout(() => {       
      modal.style.display='none';        
    }, 100);
  }


  return (


      <main className='container top1'>

          <div>

            {showItems_Welcome()}
            <Link to='/addHousehold'><button className='col-12 top1' id='addHouseHold_Welcome'><b><i class="fas fa-house-user button-img black"></i> +</b></button></Link>

          </div>

          {/* modal for editing household */}
          <div className='modals2' >                         
             <div className='row justify-centre top1'>
                <button className='col-2 btn btn-primary' onClick={()=>{closeModal()}}>ok</button>                
             </div>

             <div className='row justify-centre top2'>
                <label className='col-10 black'>Household Name</label>
             </div>
             <div className='row  justify-centre top1'>
                <input className='col-10 border'  id='input_household'></input>                
                <button className='col-1 left1 btn btn-danger'> <i class="fas fa-trash"></i></button>
             </div>

             <div className='row justify-centre top1'>

             </div>

          </div>     
      </main>    
  );
}

export default Welcome;
