import './transactions.css';


import React from "react";





function Transactions() {

  //read items from file


  var xhttp = new XMLHttpRequest();

    let household=localStorage.getItem('household');
    let path='http://localhost:8080/api/getTransactions?'+household;
    xhttp.open("GET",path );


    xhttp.send();

    xhttp.onload = function(){
      alert(this.responseText);
    }




 let items=[{item:'oranges',balance:2,total:8,minimum:5},
            {item:'apples',balance:-1,total:5,minimum:6},
            {item:'bananas',balance:-1,total:8,minimum:7},  

           ];


  //gets items from file and creates list of products         
  function getItems(items){

    let itemsList=[];
    let l=items.length;
    let c;

    for(c=0;c<l;c++){



      //populate list array to displayed
      if(items[c].balance<0){
        itemsList.push( 
          <div className='row top1 record' key={c}>         
          <p className='col margin-v-auto left vertical-centre'>{items[c].item}</p>
          <p className='col margin-v-auto left vertical-centre negative'>{items[c].balance}</p>
          <p className='col margin-v-auto left vertical-centre'>{items[c].total}</p>
          <p className='col margin-v-auto left vertical-centre'>{items[c].minimum}</p>
          <button className='col'><i class="fas fa-caret-right"></i></button>
        </div>
        );
      }
      else{
        itemsList.push(
          <div className='row top1 record' key={c}>         
          <p className='col margin-v-auto left vertical-centre'>{items[c].item}</p>
          <p className='col margin-v-auto left vertical-centre positive'>{items[c].balance}</p>
          <p className='col margin-v-auto left vertical-centre'>{items[c].total}</p>
          <p className='col margin-v-auto left vertical-centre'>{items[c].minimum}</p>
          <button className='col'><i class="fas fa-caret-right"></i></button>
        </div>
        );
      }



    }


    return(
      <div>
        {itemsList}
      </div>
    );

  }





  return (


      <main className='container top1'>
          <div className='row left'>
              <h5 className='col'>Recent transactions</h5>
          </div>

          <div className='row top2 headers sticky-top'>
              <h5 className='col left'>Item</h5>
              <h5 className='col left'>+ / -</h5>
              <h5 className='col left'>Tot</h5>
              <h5 className='col left'>Min</h5>
              <h5 className='col left'></h5>
          </div>
          <div>
            {getItems(items)}
          </div>

      </main>    
  );
}

export default Transactions;
