            <label for='total' className='col-12'>Total in stock
                <input className='col-12' type='text' name='total' id='total'></input>
                <div className='col-12'></div>
            </label>      


            {document.getElementById('total').value!='a' && <h1>React is awesome!</h1>}


        </form>

        <div className='row top1'>                
            <button className='col-3 col-lg-1 left2 green'><b>Save</b></button>
        </div> 


    </main>            
  );
}

export default ItemBalance;
