import  { useState,useEffect } from 'react';
import React from "react";
import './signUp.css';

import {
  BrowserRouter as Router,    
  Link,useParams
} from "react-router-dom";



function SignUp() {



    useEffect(()=>{
     //

    },[]);

    const [username, setusername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setpassword] = useState('');
    const [repeatpassword, setrepeatpassword] = useState('');
    const[isFilledIn,setIsFilledIn]=useState('');



    //check if all fields have been filled-in
    function checkBlank(){
        if(username!='' && email!='' && password!='' && repeatpassword!=''){
            setIsFilledIn('true');
        }
        else{
            setIsFilledIn('');
        }
    }




    return (


        <main className='container top2'>

            <div className='row maincontent'>
                <Link to='/' className='col-12'><i class="fas fa-caret-left"></i></Link>
            </div>

            <div className='row top1'> 
                <h5 className='col-12 green'><b> Sign up </b></h5>    
            </div>
            <label className='row top1 grey'>username 
                <input type='text' id='username' onKeyUp={()=>{
                    setusername(document.getElementById('username').value.trim());
                    checkBlank();
                }}></input>
            </label>            

            <label className='row top1 grey'>email
                <input type='text' id='email' onKeyUp={()=>{
                    setEmail(document.getElementById('email').value.trim());
                    checkBlank();
                }} ></input>
            </label>            

            <label className='row top1 grey'>password
                <input type='text' id='password' onKeyUp={()=>{
                    setpassword(document.getElementById('password').value.trim());
                    checkBlank();
                }}></input>
            </label>        

            {
                password!='' &&
                <label className='row top1 grey'>Repeat Password
                <input type='text' id='repeatpassword'onKeyUp={()=>{
                    setrepeatpassword(document.getElementById('repeatpassword').value.trim());
                    checkBlank();
                }}></input>
            </label> 


            }



            { isFilledIn=='true' &&
                <div className='row top1'>
                    <button className='btn btn-primary'>Sign up</button>
                </div>
            }







        </main>    
    );
  }

  export default SignUp;
