import  { useState,useEffect } from 'react';
import React from "react";
import {
    BrowserRouter as Router,    
    Link,useParams
  } from "react-router-dom";


import './Stock.css';

function StockCategory(props) {


    let { category } = useParams();
    let { subcategory } = useParams();

    const [all_StockCategory, setAll_StockCategory] = useState('');
    const[startIndex, setStartIndex]=useState(0);
    const[endIndex, setEndIndex]=useState(0);
    const [content,setContent]=useState('');



    useEffect(() => {
        ///determine folder & file path based on url parameter
        subcategory=getSubcategory(category,subcategory);

        getContent(category,subcategory);
        initialEndIndex();

     },[]);

    function initialEndIndex(){
      let indexDifference=all_StockCategory.length-startIndex;
                if(indexDifference>=3){
                setEndIndex(endIndex+3);
                }
    }

    function getContent(category,subcategory){                
        let household=localStorage.getItem('household');
        var xhttp = new XMLHttpRequest();
        let path='http://localhost:8080/api/getItems/?household='+household+'&category='+category+'&subcategory='+subcategory;
        xhttp.open("GET", path);
        xhttp.send();

        xhttp.onload = function() {       
            if(this.status==200){
                let response=JSON.parse(this.responseText);                
                response=response.sort();                   
                setAll_StockCategory(response);                                 
                showContent();     

            }  

            else{
                let response=this.responseText;
                console.log(response);
            }            
        };
    }




    function getSubcategory(category,subcategory){                
        if(category==0){
          if(subcategory=='Fruit & Vegetables'){
            return 1;
          }
          else if (subcategory=='Meat & Fish'){
            return 2;
          }
          else if (subcategory=='Bakery'){
            return 3;
          }
          else if (subcategory=='Milk & Dairy'){
            return 4;
          }
          else if (subcategory=='Salumi'){
            return 5;
          }
          else if (subcategory=='Dry Food'){
            return 6;
          }
          else if (subcategory=='Beverages'){
            return 7;
          }
          else if (subcategory=='Salt & Spices'){
            return 8;
          }
          else if (subcategory=='Glucose'){
            return 9;
          } 
          else if (subcategory=='Coffee & Tea'){
            return 10;
          }
          else if (subcategory=='Snacks'){
            return 11;
          }
          else if (subcategory=='Other'){
            return 12;
          }

        }

        //household
        else{
          if(subcategory=='Cleaning'){
            return 1;
          }
          else if (subcategory=='Hugiene'){
            return 2;
          }
          else if (subcategory=='Plastics'){
            return 3;
          }
          else if (subcategory=='Paper'){
            return 4;
          }
          else if (subcategory=='Cosmetics'){
            return 5;
          }
          else if (subcategory=='Other'){
            return 6;
          }

        }
      }

    function showContent(){

        let items=[];                          
        if(all_StockCategory!=''){                    
          let c; 

          for(c=startIndex;c<endIndex;c++){            
            let item=all_StockCategory[c];              
                item=item.split(',');
                item=item[0]; //item name                
                items.push(
                  <div>
                        <div className='row top2'>
                          <div className='col-8  record record_StockCategory vertical-centre'>
                              {item}
                            </div>

                            <button className='col-1 left1 centre btn button-secondary'>+/-</button>
                            <button className='col-1 left1 centre btn button-secondary'><i class="fas fa-cog"></i></button>
                            <button className='col-1 left1 centre btn button-secondary'><i class="fas fa-trash-alt"></i></button>
                        </div>                     
                  </div>

              );
            }

          items.push(
            <div className='row top1'>              
              <button className='col-4' onClick={()=>scrollUp()}><i class="navigateCategory col fas fa-caret-up"></i></button>
              <button className='col-4' onClick={()=>scrollDown()}><i class="navigateCategory fas fa-caret-down"></i></button>                              
            </div>  
          )
        }

        else{

          items.push(
            <div className='row top2'>
                <div className='col  centre'><small className='lightgrey'>No items</small> </div>
            </div>
          )
        }
        return(<div>{items}</div>)
    }

    function scrollUp(){

    }

    function scrollDown(){

    }



    return (        
      <main className='container top1'>

          <div className='row'>
              <Link to='/stock' className='col-12'><i class="fas fa-caret-left"></i></Link>
          </div>

          <div className='row top1'>
            <div className='col-6 green vertical-centre'>
                <b>{subcategory} </b>
            </div>

            <div className='col-6 '>
                <Link to={'/new-item/'+category+'/'+subcategory}><button className='black'><b> + Add item</b></button></Link>
                <div>{props.test}</div>
            </div>

          </div>

          < div className='row top1' id='itemsContainer_StockCategory' >
          {showContent()}
          </div>
          End index:{endIndex}

      </main>            
    );
  }



  export default StockCategory;
