import './addHousehold.css';


import React from "react";

import  { useState,useEffect } from 'react';

import {Link} from "react-router-dom";



function Welcome() {


function test(){
    let text= document.getElementById('householdName').value;
    let submit=document.getElementById('submit_AddHousehold');
    if(text!=''){
        submit.disabled=false;
    }
    else{
        submit.disabled=true;
    }

}

  return (


      <main className='container top1'>
          <div className='row'>
              <Link to='/' className='col-12'><i class="fas fa-caret-left"></i></Link>
          </div>
          <div className='row'>
                <div className='col-12'>
                    <h5>Add a new Household </h5>
                </div>                            
          </div>

          <form method='post' action='http://localhost:8080/api/postHousehold' className='row'>
                    <label for='householdName'>Household Name
                        <input className='col-12' type='text' id='householdName' name='householdName' onKeyUp={()=>{test()}}></input>
                    </label>
                    <button className='top1 col-12  button-secondary' id='submit_AddHousehold' type='submit' disabled>Add</button>

          </form>



      </main>    
  );
}

export default Welcome;
