import './welcome.css';


import React from "react";

import  { useState,useEffect } from 'react';

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

function Welcome() {


  const [all_Welcome, getAll_Welcome] = useState('');
//   const [selectedHousehold, select_Welcome] = useState('');    

    getHouseholds();

  function getHouseholds(){

    var xhttp = new XMLHttpRequest();
    xhttp.onload = function() {
        if(this.status==200){
            let response=this.responseText;
            getAll_Welcome(response);            
        }
    };
    xhttp.open("GET", "http://localhost:8080/api/getHouseholds");
    xhttp.send();

  }  


  function displayHouseholds(){
      let households=[];

      let l = all_Welcome.length;
      let c;

      for(c=0;c<l;c++){

        households.push(all_Welcome[c]);

      }

      return households;

  }





  return (


      <main className='container top1'>

          <div className='row'>

             {/* <Link to={'/transactions/'+selectedHousehold}>

                 <button className='col-12 border'>
                    <i class="fas fa-house-user button-img green"></i> <br></br> <span className='black'>{selectedHousehold}</span> 
                 </button>
                 <button className='col-12 border grey centre'>
                       <i className="far fa-edit"></i>
                 </button>                 
            </Link> */}

            {displayHouseholds()}
            <Link><button className='col-12 top1'><b>+ Add New Household</b></button></Link>

          </div>

      </main>    
  );
}

export default Welcome;
