import './transactions.css';


import React from "react";


function Transactions() {

 let items=[{item:'oranges',balance:2,total:8,minimum:5},
            {item:'apples',balance:-1,total:5,minimum:6},
            {item:'bananas',balance:-1,total:8,minimum:7},  
           ];


  function getItems(items){

    // let itemsList=items.map((item)=>{
      // <div className='row record' key={item}> 
      //   <p className='col margin-v-auto left vertical-centre'>{item.item}</p>
      //   <p className='col margin-v-auto left vertical-centre'>{item.balance}</p>
      //   <p className='col margin-v-auto left vertical-centre'>{item.total}</p>
      //   <p className='col margin-v-auto left vertical-centre'>{item.mininum}</p>
      //   <button className='col'><i class="fas fa-caret-right"></i></button>
      // </div>
    // })
    let itemsList=[];
    let l=items.length;
    let c;

    for(c=0;c<l;c++){
      itemsList.push(


        <div className='row record' key={c}> 
        <p className='col margin-v-auto left vertical-centre'>{items[c].item}</p>
        <p className='col margin-v-auto left vertical-centre'>{items[c].balance}</p>
        <p className='col margin-v-auto left vertical-centre'>{items[c].total}</p>
        <p className='col margin-v-auto left vertical-centre'>{items[c].minimum}</p>
        <button className='col'><i class="fas fa-caret-right"></i></button>
      </div>
      )
    }


    return(
      <div>
        {itemsList}
      </div>
    );

  }





  return (
    <div className="container">

      <main className='container'>
          <div className='row left'>
              <h3 className='col'>Recent transactions</h3>
          </div>

          <div className='row top2'>
              <h5 className='col left'>Item</h5>
              <h5 className='col left'>+ / -</h5>
              <h5 className='col left'>Total</h5>
              <h5 className='col left'>Minimum</h5>
              <h5 className='col left'></h5>
          </div>
          <div>
            {getItems(items)}
          </div>
          {/* <div className='row record'> 
            <p className='col margin-v-auto left vertical-centre'>1</p>
            <p className='col margin-v-auto left vertical-centre'>+2</p>
            <p className='col margin-v-auto left vertical-centre'>8</p>
            <p className='col margin-v-auto left vertical-centre'>5</p>
            <button className='col'><i class="fas fa-caret-right"></i></button>
          </div> */}


      </main>




    </div>
  );
}

export default Transactions;
