import './transactions.css';
import  { useState,useEffect } from 'react';

import React from "react";

import {BrowserRouter as Router,Link,useParams} from "react-router-dom";


function Favourites() {

  const [all_Favourites, getAll_Favourites] = useState('');

  useEffect(()=>{
    getFavourites();      
  },[]);


  //read items from file
  function getFavourites(){
    let items=[];
    var xhttp = new XMLHttpRequest();
    let household=localStorage.getItem('household');

    let path='http://localhost:8080/api/getFavourites?household='+household;
    xhttp.open("GET",path );
    xhttp.send();
    xhttp.onload = function(){
      if(this.status!=200){
        console.log(this.responseText);
      }
      else{
        let response=JSON.parse(this.responseText);
        console.log(response);
        getAll_Favourites(response);        
      }
    }
  }


  function getSubcategory(category,subcategory){                
        if(category==0){
        if(subcategory=='Fruit - Vegetables'){
            return 1;
        }
        else if (subcategory=='Meat - Fish'){
            return 2;
        }
        else if (subcategory=='Bakery'){
            return 3;
        }
        else if (subcategory=='Milk - Dairy'){
            return 4;
        }
        else if (subcategory=='Salumi'){
            return 5;
        }
        else if (subcategory=='Dry Food'){
            return 6;
        }
        else if (subcategory=='Beverages'){
            return 7;
        }
        else if (subcategory=='Salt - Spices'){
            return 8;
        }
        else if (subcategory=='Glucose'){
            return 9;
        } 
        else if (subcategory=='Coffee - Tea'){
            return 10;
        }
        else if (subcategory=='Snacks'){
            return 11;
        }
        else if (subcategory=='Other'){
            return 12;
        }

     }

    //household
    else{
      if(subcategory=='Cleaning'){
        return 1;
      }
      else if (subcategory=='Hugiene'){
        return 2;
      }
      else if (subcategory=='Plastics'){
        return 3;
      }
      else if (subcategory=='Paper'){
        return 4;
      }
      else if (subcategory=='Cosmetics'){
        return 5;
      }
      else if (subcategory=='Other'){
        return 6;
      }

    }
}

  //gets items from file and creates list of favourites         
  function getItems(all_Favourites){
    if(all_Favourites!='')
    {  
      let itemsList=[];
      let l=all_Favourites.length;      
      let c;
      for(c=0;c<l;c++){            
        let  favourites= all_Favourites[c].split(',')
          let item=favourites[0];
          let category = favourites[1];
          let subcategory = getSubcategory(category, favourites[2]);          
          //populate list array to displayed
          itemsList.push( 
            <div className='row top1 record' key={c}>         
              <p className='col-8  vertical-centre'>{item}</p>       



              <div className='col background-white'>
                  <Link  to={'/item-balance/'+category+'/'+subcategory+'/'+item} ><button className='col centre btn button-secondary'>+</button></Link>
                  <Link  to={'/item-settings/'+category+'/'+subcategory+'/'+item}><button className='col left2 centre btn button-secondary'><i class="fas fa-cog"></i></button></Link>
                  <Link ><button className='col left2 centre btn button-secondary' ><i class="fas fa-trash-alt"></i></button></Link>
              </div>
          </div>
          );          
      }


      return(
        <div>
          {itemsList}
        </div>
      );
    }else{
      return(
          <div className='row top1'>         
              <p className='col margin-v-auto  centre lightgrey'>No favourites transactions found</p>              
          </div>
      )
    }

  }





  return (


      <main className='container top1'>
          <div className='row left'>
              <h5 className='col'>Favourite Items</h5>
          </div>

          <div className='row top2 headers sticky-top'>
              <h5 className='col left'>Item</h5>              
              <h5 className='col left'></h5>
          </div>
          <div id='main-container_Transactions'>
            {getItems(all_Favourites)}            
          </div>

          { all_Favourites!='' &&
            <div className='row'>
              <div className='col lightgrey'><small className='left1'>scroll down to see more transactions</small></div>
            </div>
          }
      </main>    
  );
}

export default Favourites;
