// import './main.css';

import  { useState,useEffect } from 'react';
import React from "react";
import './itemBalance.css';

import {
  BrowserRouter as Router,    
  Link,useParams
} from "react-router-dom";







function ItemBalance() {

  let { category,subcategory,item } = useParams();

  const [total, getTotal] = useState(0);  

  let household=localStorage.getItem('household');




  useEffect(() => {

    //get the stock total for this item

    let household=localStorage.getItem('household');
    let path='http://localhost:8080/api/getBalance?household='+
        household+'&category='+category+'&subcategory='+subcategory+
        '&item='+item;


    var xhttp = new XMLHttpRequest();    
    xhttp.open("GET", path);
    xhttp.send();

    xhttp.onload = function() {

      let response=parseInt(this.responseText);
      getTotal(response);
    };


    },[]);

  return (        
    <main className='container top1'>

        <div className='row'>
              <Link to={'/stock-category/'+category+'/'+subcategory} className='col-12'><i class="fas fa-caret-left"></i></Link>              
        </div>

        <div className='row top1'> 
            <div className='col green'><b> {item} </b><span className='lightgrey'> |stock</span></div>
        </div>

        <form method='post' action='http://localhost:8080/api/putBalance' className='border row top1' id='form_ItemNew' >
            <label for='total' className='col-12 grey'>Total in stock
                <input className='hidden' type='text' name='total' id='total' value={total}></input>
                <div className='col-12'> {total}</div>
            </label>      

            <button type='button' onClick={()=>{getTotal(total+5)}}>+5</button>




        </form>

        <div className='row top1'>                
            <button  className='col-3 col-lg-1 left2 btn button-secondary'><b>Save</b></button>
        </div> 


    </main>            
  );
}

export default ItemBalance;
