import  { useState,useEffect } from 'react';
import React from "react";
import './login.css';

import {
  BrowserRouter as Router,    
  Link,useParams
} from "react-router-dom";



function Login() {



    useEffect(()=>{
     //


    checkLoggedUser();

    },[]);

    const [email,setEmail]=useState('');

    const [password,setPassword]=useState('');


    //control if user is already logged in
    function checkLoggedUser(){
        let isLoggedIn = localStorage.getItem('isLoggedIn');
        if(isLoggedIn=='true'){


            let email=localStorage.getItem('email');


            // window.location.href = "/welcome";

        }     


    }


    function submitLogin(){


        var xhttp = new XMLHttpRequest();

            xhttp.open("POST", "http://localhost:8080/api/postUser");
            xhttp.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
            let data=`username=${username}&email=${email}&password=${password}`;

            xhttp.send(data);
            xhttp.onload = function() {
            if(this.status==200){

                let origin='index';
                let response=JSON.parse(this.responseText);        
                let message=response.message;
                let path='http://localhost:3000/error/'+origin+'/'+message;
                window.location.replace(path);
            }
            else{
                let origin='index';
                let response=JSON.parse(this.responseText);        
                let message=response.message;
                setErrorMessage(message);
                openModal(0)                
            }
        }

    }




    return (


        <main className='container top2'>
            <div className='row left'>
                <h5 className='col left green'><b>Sign in</b></h5>
            </div>

            <label className='row top1 grey'>Email    
                    <input type='text' id='email' onKeyUp={()=>{
                        setEmail(document.getElementById('email').value.trim());
                    }}></input>            
            </label>

            <label className='row top1 grey'>Password    
                    <input type='text' id='password' onKeyUp={()=>{
                        setPassword(document.getElementById('password').value.trim());
                    }}></input>            
            </label>

            <div className='row top1'>
                <button className='btn btn-primary' onClick={()=>{submitLogin()}}  >ok</button>
            </div>


            <Link to='/signup' className='col-4 col-lg'>
                <div className='row top1'>
                    <button className='btn green'>Sign up</button>
                </div>    
            </Link>

            <div className='row top1'>
                <button className='btn green' onClick={()=>{localStorage.clear()}}>clear</button>
            </div>






        </main>    
    );
  }

  export default Login;
