import './main.css';
import logo from './images/logo.png';

import React from "react";
import  { useState,useEffect } from 'react';



function Header() {

  const [username, setUsername] = useState('');  
  const [email, setEmail] = useState('');  
  const [changePassword,setChangePasseword] = useState('Change password');



  useEffect(()=>{

    //check if user is logged in and add username to header
   if(localStorage.getItem('username')!=null){
    setUsername(localStorage.getItem('username'));    
    }  

  },[]);

  function openUserModal(){
    let modal=document.getElementById('userModal');

    //hide elements with sticky-top class
    let sticky=document.getElementsByClassName('sticky-top');
      sticky=sticky[0];
    if(sticky!=undefined){    
      sticky.style.visibility='hidden';
    }
      modal.style.display='block';
      setTimeout(() => {
        modal.style.transform='translateY(0)';
        modal.style.opacity='1';
      }, 100);
}


function closeUserModal(){  
  let modal=document.getElementById('userModal');
  let sticky=document.getElementsByClassName('sticky-top');  
  modal.style.transform='translateY(-10px)';
  modal.style.opacity='0';          
  setTimeout(() => {       
    modal.style.display='none';  
    sticky=sticky[0];
    if(sticky!=undefined){

      sticky.style.visibility='visible';                          
    }


  }, 100);

}

function logout(){
  localStorage.clear();
  window.location.href = "/";   

}

function openModalAccount(){
  let modal=document.getElementById('modalAccount');

    modal.style.display='block';
    setTimeout(() => {
      modal.style.transform='translateX(0)';
      modal.style.opacity='1';
    }, 100);
}


function togglePassword(){
  if(changePassword=='Change password'){
    setChangePasseword('Cancel');
  }
  else{
    setChangePasseword('Change password');
  }
}


  return (    


    <header className='container top2  maincontent'>

        <div id='userModal'>
                <div className='row centre  top2'>
                    <p className='green' ><b>{username}</b></p>
                </div>

                <div className='row centre top1'>       
                  <button className='col btn black' onClick={()=>{openModalAccount()}} ><i class="fas fa-cog"></i></button>               

                </div>

                <div className='row centre top1'>       
                  <button className='col btn black' onClick={()=>{logout()}}><i class="fas fa-sign-out-alt"></i></button>                                 
                </div>


                <div className='row justify-centre top2'>       
                    <button className='col-3  btn btn-primary' onClick={()=>{closeUserModal()}}>Cancel</button>               
                </div>
        </div> 


        {/* modal for editing user account */}
          <div id='modalAccount'>                                          
             <div className='row  top2'>
                <label className='col-8 left2  black'><b>Edit account information</b></label>
             </div>

             <div className='row  top2'>
                <label className='col-8 left2  black'>Username</label>
                <input className='col-8 left2 top1' id='username'></input>
             </div>


             <div className='row  top1'>
                <button className='col-8 left2 btn button-secondary left' onClick={()=>togglePassword()}>{changePassword}</button>
             </div>

             <div className='row  top1'>
                <label className='col-8 left2  black'>Current Password</label>
                <input className='col-8 left2 top1' id='username'></input>
             </div>

             <div className='row  top1'>
                <label className='col-8 left2  black'>New Password</label>
                <input className='col-8 left2 top1' id='username'></input>
             </div>

             <div className='row  top1'>
                <label className='col-8 left2  black'>Repeat new password</label>
                <input className='col-8 left2 top1' id='username'></input>
             </div>

             {/* modal for displaying warning before deleting a household */}     
             <div className='modals'>                         
              <div className='row centre top2'>
                  <p className='col' ><b>Are you sure you want to delete this household?</b></p>
              </div>
              <div className='row justify-centre   top1'>                
                  <button className='col-3 left1 btn btn-primary' >Yes</button>               
                  <button className='col-3 left1 btn btn-primary2' >cancel</button>                 
              </div>
            </div>  
          </div>  


      {
        username=='' &&
        <div className='container'><img src={logo} className='button' id='logo'></img><span className='button'> <b>Estia</b></span></div>
      }

      {
        username!='' &&
        <div className='container'><img src={logo} className='button' id='logo' onClick={()=>{openUserModal()}}></img><span className='lightgrey button' onClick={()=>{openUserModal()}}> |{username}</span> </div>
      }


    </header>            
  );
}

export default Header;
