// import './main.css';


import React from "react";

import groceries from './images/stock/groceries1.png';
import household from './images/stock/household.png'

import  { useState,useEffect } from 'react';


function Stock() {

  const [selected_Stock, select_Stock] = useState(0);  
  const [commodity, setCommodity] = useState('Groceries');
  const [indexDisplayed_Stock, setIndex_Stock] = useState(0);

  const [groceries, getGroceries] = useState([{name:"Fruit/Vegetables"},
                                              {name:"Dairy"},
                                              {name:"Dairy"},
                                              {name:"Dairy"},
                                              {name:"Dairy"},
                                              ]);




  useEffect(() => {
    let commodityType=document.getElementsByClassName('commodityType');
    let l=commodityType.length;
    let c;
    for(c=0;c<l;c++){
      commodityType[c].style.color='black';
    }
    commodityType[selected_Stock].style.color='darkcyan';

    if(selected_Stock==0){
      setCommodity('Groceries');
    }
    else{
      setCommodity('Household');
    }

    });

  function showGroceries (){

    let groceriesList=[];
    let l=groceries.length
    let c;

    for(c=indexDisplayed_Stock;c<indexDisplayed_Stock+3;c++){
      //populate list array to displayed

        groceriesList.push(
          <div className='row top1' key={c}>         
            <button className='col full'>{groceries[c].name}</button>          
          </div>
        );
    }

    return(
      <div>
        {groceriesList}
      </div>
    );

  }  

  return (        
    <main className='container top2'>
        <div className='row left'>
            <h5 className='col'>Stock</h5>
        </div>

        <div className='row'>    

                <div className='col right'>
                  <button className='right2 commodityType' onClick={()=>select_Stock(0)} >      
                    <i className="fas fa-lemon "></i>
                  </button>

                  <button className='commodityType' onClick={()=>select_Stock(1)} >
                    <i class="fas fa-pump-soap"></i>
                  </button>
                </div>
        </div>

        <div className='row top1'>
              <p className='col'> {commodity}</p> 
        </div> 

        <div className='row '>
          <div className='col-6'>
            {showGroceries()}
          </div>

          <div className='col-6 vertical-centre'>

              <div className='row'>
                <button className='col'><i class="navigateCategory fas fa-caret-down"></i></button>
                <button className='col'><i class="navigateCategory col fas fa-caret-up"></i></button>
              </div>

          </div> 
        </div>



    </main>            
  );
}

export default Stock;
