import './transactions.css';
import  { useState,useEffect } from 'react';
import {BrowserRouter as Router,Link,useParams} from "react-router-dom";
import React from "react";

function Shoppinglist() {
  const [all_Shoppinglist, getAll_Shoppinglist] = useState('');
  let household=localStorage.getItem('household');

  useEffect(()=>{
    getShoppinglist();
    // getNotifications();

  },[]);



  function getShoppinglist(){
    let items=[];
    var xhttp = new XMLHttpRequest();

    let path='http://localhost:8080/api/getShoppinglist?household='+household;
    xhttp.open("GET",path );
    xhttp.send();
    xhttp.onload = function(){
      if(this.status!=200){
        console.log(this.responseText);
      }
      else{
        let response=JSON.parse(this.responseText);
        console.log(response);
        getAll_Shoppinglist(response);        
      }
    }
  }

  //gets items from file and creates list of products         
  function getItems(all_Shoppinglist){
    if(all_Shoppinglist!='')
    {  
      let itemsList=[];

      let l=all_Shoppinglist.length;

      let c;

      for(c=0;c<l;c++){


        let  shoppinglist= all_Shoppinglist[c].split(',')
          let item=shoppinglist[0];          
          let category=shoppinglist[1];
          let subcategory=shoppinglist[2];


          //convert category & subcategory numerical and literal value respectively (to be used as parameters for the link later on)
          category=getCategory(category);          
          subcategory=getSubcategory(category,subcategory);

          //populate list array to displayed                               
                itemsList.push(

                  <div className='row top1 record' key={c}>         
                    <p className='col-8 col-md-9  left1 margin-v-auto left vertical-centre'><small>{item}</small></p>                                                      
                    <div className='col border background-white'>
                      <button className='btn col button-secondary'><i class="fas fa-cart-arrow-down"></i></button>
                      <button className='col border green'><b> 1</b></button>                      
                    </div>
                    <div className='col border background-white centre'>
                    <button className='left2 btn button-secondary2'><i class="fas fa-minus-circle"></i></button>
                    </div>


                  </div>

              );

      }


      return(
        <div>
          <div className='row top2 headers sticky-top'>
              <h6 className='col left'>Item</h6>              
              <h6 className='col left'></h6>
          </div>
          {itemsList}
        </div>
      );
    }else{
      return(
          <div className='row top1'>         
              <p className='col margin-v-auto  left lightgrey'>Your shopping list is empty</p>              
          </div>
      )
    }

  }



  function getCategory(category){
    if(category=='groceries'){
        return 0;
    }
    else{
        return 1;
    }
  }


  function getSubcategory(category,subcategory){                
    if(category=='0'){
    if(subcategory=='1'){
        return 'Fruit - Vegetables';
    }
    else if (subcategory=='2'){
        return 'Meat - Fish';
    }
    else if (subcategory=='3'){
        return 'Bakery';
    }
    else if (subcategory=='4'){
        return 'Milk - Dairy';
    }
    else if (subcategory=='5'){
        return 'Salumi';
    }
    else if (subcategory=='6'){
        return 'Dry Food';
    }
    else if (subcategory=='7'){
        return 'Beverages';
    }
    else if (subcategory=='8'){
        return 'Salt - Spices';
    }
    else if (subcategory=='9'){
        return 'Glucose';
    } 
    else if (subcategory=='10'){
        return 'Coffee - Tea';
    }
    else if (subcategory=='11'){
        return 'Snacks';
    }
    else if (subcategory=='12'){
        return 'Other';
    }

 }

//household
else{
  if(subcategory=='1'){
    return 'Cleaning';
  }
  else if (subcategory=='2'){
    return 'Hugiene';
  }
  else if (subcategory=='3'){
    return 'Plastics';
  }
  else if (subcategory=='4'){
    return 'Paper';
  }
  else if (subcategory=='5'){
    return 'Cosmetics';
  }
  else if (subcategory=='6'){
    return 'Other';
  }

}

}

  return (


      <main className='container top1'>
          <div className='row left'>
              <h5 className='col left'>Shopping List</h5>
          </div>         
          <div id='main-container_Shoppinglist'>
            {getItems(all_Shoppinglist)}            
          </div>

          { all_Shoppinglist!='' &&
            <div className='row'>
              <div className='col lightgrey'><small className='left1'>scroll down to see more items</small></div>
            </div>
          }

          { all_Shoppinglist!='' &&  
            <div className='row top2'>
                <div className='col-8 col-md-9 right'></div>
                <div className='col'>
                  <button className='col-12 btn btn-primary'><b>Done</b></button>
                </div>
            </div>
          } 
      </main>    
  );
}

export default Shoppinglist;
