// import './main.css';


import React from "react";

// import groceries from './images/stock/groceries1.png';
// import household from './images/stock/household.png'

import  { useState,useEffect } from 'react';


function Stock() {

  const [selected_Stock, select_Stock] = useState(0);  
  const [commodity, setCommodity] = useState('Groceries');
  const [indexDisplayed_Stock, setIndex_Stock] = useState(0);
  const [listDisplayed_Stock, setList_Stock] = useState(0);

  const [groceries, getGroceriesList] = useState([{name:"Fruit/Vegetables"},
                                              {name:"Legumes"},
                                              {name:"Milk & Dairy"},
                                              {name:"Bakery"},                                                                                            
                                              {name:"Meat & Fish"},
                                              {name:"Drinks"},
                                              {name:"Salt, Sugar & Spices"},
                                              {name:"Sweets"},
                                              {name:"Dry Food"},                                                                                            
                                              {name:"Other"},

                                              ]);

  const [household, getHouseholdList] = useState([{name:"Cleaning products"},
                                              {name:"Personal Hygiene"},
                                              {name:"Disposables"},

                                              ]);                                          



  useEffect(() => {
    let commodityType=document.getElementsByClassName('commodityType');
    let l=commodityType.length;
    let c;
    for(c=0;c<l;c++){
      commodityType[c].style.color='black';
    }
    commodityType[selected_Stock].style.color='lightseagreen';
    });


  function changeStock(){

    if(selected_Stock==0){
      select_Stock(1);
      setCommodity('Household');      
      setList_Stock(1);  
      setIndex_Stock(0);          
    }
    else{
      select_Stock(0);
      setCommodity('Groceries');      
      setList_Stock(0);
      setIndex_Stock(0);

    }

  }  
  function showItems_Stock (){

    let itemsList=[];
    let source;
    if(listDisplayed_Stock==0){
      source=groceries;
    }
    else{
      source=household;
    }

    let l=source.length;
    let c;

    for(c=0;c<l;c++){

      itemsList.push(
        <div className='row top1 record-stock' key={indexDisplayed_Stock}>         
          <button className='col left full green'><b>{source[c].name}</b></button>          
        </div>
      );

    }


    return(
      <div>
        {itemsList}
      </div>
    );

  }  


  function indexUp(){
    let source;
    if(listDisplayed_Stock==0){
      source=groceries;
    }
    else{
      source=household;
    }

    let l=source.length;
    if(indexDisplayed_Stock<l-1){
      setIndex_Stock(indexDisplayed_Stock+1);
    }
  }

  function indexDown(){

    if(indexDisplayed_Stock>0){
      setIndex_Stock(indexDisplayed_Stock-1);
    }
  }

  return (        
    <main className='container top2'>
        <div className='row left'>
            <h5 className='col'>Stock</h5>
        </div>

        <div className='row'>    

                <div className='col right'>
                  <button className='right2 commodityType navigateCategory' onClick={()=>changeStock()} >      
                    <i className="fas fa-lemon "></i>
                  </button>

                  <button className='commodityType navigateCategory' onClick={()=>changeStock()} >
                    <i class="fas fa-pump-soap"></i>
                  </button>
                </div>
        </div>

        <div className='row top1'>
              <p className='col'> {commodity}</p> 
        </div> 

        <div className='row '>
          <div className='col-6 col-lg-4 vertical-centre'>
            {showItems_Stock()}
          </div>

          <div className='col-6 col-lg  vertical-centre'>

              <div className='row  justify-centre'>
              <button className='col col-lg-3' onClick={()=>indexDown()}><i class="navigateCategory col fas fa-caret-up"></i></button>
                <button className='col col-lg-3' onClick={()=>indexUp()}><i class="navigateCategory fas fa-caret-down"></i></button>                
              </div>

          </div> 
        </div>



    </main>            
  );
}

export default Stock;
