// import './main.css';

import  { useState,useEffect } from 'react';
import React from "react";
import './itemNew.css';

import {
  BrowserRouter as Router,    
  Link,useParams
} from "react-router-dom";







function ItemNew() {

    let { category } = useParams();

  const [selected_ItemNew, select_ItemNew] = useState(0);  





  function enableNext_ItemNew(e){
    alert(e.target)
  }





  useEffect(() => {
    let containers= document.getElementsByClassName('form-field-container_ItemNew');
    let l= containers.length;
    let c;

        for (c=0;c<l;c++){
         containers[c].classList.add('hidden');
        }

    containers[selected_ItemNew].classList.remove('hidden');
    });


    function next_ItemNew(){
        if(selected_ItemNew<4)
        select_ItemNew(selected_ItemNew+1);

    }

    function previous_ItemNew(){
        if(selected_ItemNew>0){
            select_ItemNew(selected_ItemNew-1);
        }

    }


  return (        
    <main className='container top1'>

        <div className='row'>
              <Link to={'/stock-category/'+category} className='col-12'><i class="fas fa-caret-left"></i></Link>              
        </div>

        <div className='row top1'>
            <div className='col green'><b>New item</b><span className='lightgrey'> |{category}</span></div>
        </div>

        <form className='row top1 border' id='form_ItemNew'>

            <div className='col-12 border form-field-container_ItemNew'>
                <label className='col-11 grey label_ItemNew'>Name
                    <input type='text' className='col-12' onKeyUp={(e)=>{enableNext_ItemNew(e)}}></input>                    
                </label>
                <button className='left1'><i class="fas fa-info-circle lightgrey button-img"></i></button>                
            </div>

            <div className='col-12 border form-field-container_ItemNew hidden'>
                <label className='col-11 grey label_ItemNew'>Total in stock
                    <input type='text' className='col-12'></input>                    
                </label>
                <button className='left1'><i class="fas fa-info-circle lightgrey button-img"></i></button>                
            </div>

            <div className='col-12 border form-field-container_ItemNew hidden'>
                <label className='col-11 grey label_ItemNew'>Minimum quantity allowed
                    <input type='text' className='col-12'></input>                    
                </label>
                <button className='left1'><i class="fas fa-info-circle lightgrey button-img"></i></button>                
            </div>

            <div className='col-12 border form-field-container_ItemNew hidden'>
                <label className='col-11 grey label_ItemNew'>Group quantity
                    <input type='text' className='col-12'></input>                    
                </label>
                <button className='left1'><i class="fas fa-info-circle lightgrey button-img"></i></button>                
            </div>

            <div className='col-12 border form-field-container_ItemNew hidden'>
                <label className='col-11 grey label_ItemNew'>Duration (days)
                    <input type='text' className='col-12'></input>                    
                </label>
                <button className='left1'><i class="fas fa-info-circle lightgrey button-img"></i></button>                
            </div>

        </form>

        <div className='row top1'>
        <button id='button-previous_ItemNew' className='col-2' onClick={()=>{previous_ItemNew()}}><i class="fas fa-angle-left button-img"></i></button>
        <button id='button-next_ItemNew' className='col-2 left2' onClick={()=>{next_ItemNew()}}   disabled><i class="fas fa-angle-right button-img"></i></button>
        <button className='border col-2 left2 btn btn-primary'><b>Done</b></button>
        </div> 


    </main>            
  );
}

export default ItemNew;
