import './transactions.css';
import  { useState,useEffect } from 'react';

import React from "react";

function Transactions() {
  const [all_Transactions, getAll_Transactions] = useState('');

  useEffect(()=>{
    getTransactions();
    getNotifications();

  },[]);

  function getNotifications(){    
      let household=localStorage.getItem('household');
      let path='http://localhost:8080/api/getNotifications?household='+household;
      var xhttp = new XMLHttpRequest();    
      xhttp.open("GET", path);
      xhttp.send();
      xhttp.onload = function() {  
         if(this.status==200){
            console.log('retrieved notifications');
         }
      };   
  }

  function getTransactions(){
    let items=[];
    var xhttp = new XMLHttpRequest();
    let household=localStorage.getItem('household');
    let path='http://localhost:8080/api/getTransactions?household='+household;
    xhttp.open("GET",path );
    xhttp.send();
    xhttp.onload = function(){
      if(this.status!=200){
        console.log(this.responseText);
      }
      else{
        let response=JSON.parse(this.responseText);
        console.log(response);
        getAll_Transactions(response);        
      }
    }
  }

  //gets items from file and creates list of products         
  function getItems(all_Transactions){
    if(all_Transactions!='')
    {  
      let itemsList=[];

      let l=all_Transactions.length;

      let c;

      for(c=0;c<l;c++){


        let  transactions= all_Transactions[c].split(',')
          let item=transactions[0];
          let transaction=transactions[1];
          let total=transactions[2];
          let minimum=transactions[3];
          let category=transactions[4];
          let subcategory=transactions[5];
          let deleted=transactions[6];


          //populate list array to displayed

          if(transaction<0){ //items were subtracted from stock total
            if(deleted=='false')
              {  
                itemsList.push( 
                <div className='row top1 record' key={c}>         
                  <p className='col left1 margin-v-auto left vertical-centre'>{item}</p>
                  <p className='col margin-v-auto left vertical-centre negative'>{transaction}</p>
                  <p className='col margin-v-auto left vertical-centre'>{total}</p>
                  <p className='col margin-v-auto left vertical-centre'>{minimum}</p>
                  <button className='col'><i class="fas fa-caret-right"></i></button>
              </div>
              );
            }
            else{

              itemsList.push( 
                <div className='row top1 record' key={c}>         
                  <p className='col left1 margin-v-auto left vertical-centre'>{item}</p>
                  <p className='col margin-v-auto left vertical-centre negative'>{transaction}</p>
                  <p className='col margin-v-auto left vertical-centre'>{total}</p>
                  <p className='col margin-v-auto left vertical-centre'>{minimum}</p>
                  <div className='col grey background-white'> <small>item deleted</small> </div>
              </div>
              );
            }
          }
          else{ //items were added to stock total
            if(deleted=='false')
            {
                itemsList.push(
                <div className='row top1 record' key={c}>         
                  <p className='col left1 margin-v-auto left vertical-centre'>{item}</p>
                  <p className='col margin-v-auto left vertical-centre positive'>{transaction}</p>
                  <p className='col margin-v-auto left vertical-centre'>{total}</p>
                  <p className='col margin-v-auto left vertical-centre'>{minimum}</p>
                  <button className='col btn button-secondary  left'>+ / -</button>
              </div>
              );
            }
            else{
              itemsList.push(
                <div className='row top1 record' key={c}>         
                  <p className='col left1 margin-v-auto left vertical-centre'>{item}</p>
                  <p className='col margin-v-auto left vertical-centre positive'>{transaction}</p>
                  <p className='col margin-v-auto left vertical-centre'>{total}</p>
                  <p className='col margin-v-auto left vertical-centre'>{minimum}</p>
                  <div className='col grey background-white'> <small>item deleted</small> </div>
              </div>
              );
            }
          }

      }


      return(
        <div>
          <div className='row top2 headers sticky-top'>
              <h5 className='col left'>Item</h5>
              <h5 className='col left'>+ / -</h5>
              <h5 className='col left'>Tot</h5>
              <h5 className='col left'>Min</h5>
              <h5 className='col left'></h5>
          </div>
          {itemsList}
        </div>
      );
    }else{
      return(
          <div className='row top1'>         
              <p className='col margin-v-auto  left lightgrey'>No recent transactions found</p>              
          </div>
      )
    }

  }

  return (


      <main className='container top1'>
          <div className='row left'>
              <h5 className='col left'>Recent transactions</h5>
          </div>         
          <div id='main-container_Transactions'>
            {getItems(all_Transactions)}            
          </div>

          { all_Transactions!='' &&
            <div className='row'>
              <div className='col lightgrey'><small className='left1'>scroll down to see more transactions</small></div>
            </div>
          }
      </main>    
  );
}

export default Transactions;
