import './main.css';
import logo from './images/logo.png';

import React from "react";
import  { useState,useEffect } from 'react';



function Header() {

  const [username, setUsername] = useState('');  
  const [email, setEmail] = useState('');  


  useEffect(()=>{

    //check if user is logged in and add username to header
   if(localStorage.getItem('username')!=null){
    setUsername(localStorage.getItem('username'));

    }    
  },[]);






  return (        
    <header className='container top2  maincontent'>
      {
        username=='' &&
        <div className='container'><img src={logo} className='button' id='logo'></img><span className='button'> <b>Estia</b></span></div>
      }

      {
        username!='' &&
        <div className='container border'><img src={logo} className='button' id='logo'></img><span className='lightgrey button margin-right1'> |{username}</span> <span className='black button'> <i class="fas fa-sign-out-alt"></i></span> </div>
      }


    </header>            
  );
}

export default Header;
