import './main.css';
import logo from './images/logo.png';

import React from "react";
import  { useState,useEffect } from 'react';



function Header() {

  const [username, setUsername] = useState('');  
  const [email, setEmail] = useState('');  


  useEffect(()=>{

    //check if user is logged in and add username to header
   if(localStorage.getItem('username')!=null){
    setUsername(localStorage.getItem('username'));

    }    
  },[]);



  function openModal(){
    let modal=document.getElementsByClassName('modals2');

    //hide elements with sticky-top class
    let sticky=document.getElementsByClassName('sticky-top');
    sticky=sticky[0];
    sticky.style.visibility='hidden';

      modal=modal[0];
      modal.style.display='block';
      setTimeout(() => {
        modal.style.transform='translateY(0)';
        modal.style.opacity='1';
      }, 100);
}


function closeModal(){  
  let modal=document.getElementsByClassName('modals2');
  modal=modal[0];    
  modal.style.transform='translateY(-10px)';
  modal.style.opacity='0';          
  setTimeout(() => {       
    modal.style.display='none';  
    sticky=sticky[0];
    sticky.style.visibility='visible';                          
  }, 100);

}


  return (    


    <header className='container top2  maincontent'>

        <div className='modals2'>
                <div className='row centre top2'>
                    <p className='col' ><b>{username}</b></p>
                </div>
                <div className='row justify-centre   top1'>                
                    <button className='col-3 left1 btn btn-primary' onClick={()=>{closeModal()}}>OK</button>               
                </div>
        </div> 

      {
        username=='' &&
        <div className='container'><img src={logo} className='button' id='logo'></img><span className='button'> <b>Estia</b></span></div>
      }

      {
        username!='' &&
        <div className='container'><img src={logo} className='button' id='logo'></img><span className='lightgrey button' onClick={()=>{openModal()}}> |{username}</span> </div>
      }


    </header>            
  );
}

export default Header;
