// import './main.css';

import  { useState,useEffect } from 'react';
import React from "react";
import './itemSettings.css';

import {
  BrowserRouter as Router,    
  Link,useParams
} from "react-router-dom";

function ItemSettings() {

  let { category,subcategory,item } = useParams();

  const [newItemName, setNewItemName] = useState(item);  
  const [index,setIndex]=useState(1);

  let household=localStorage.getItem('household');


  function setNewItemNamef(){
      let input=document.getElementById('item');
      setNewItemName(input.value.trim());
  }  

  function setIndexf(n){
      if (index<8 && (index+index)>0){
        setIndex(index+n)
      }
  }

  useEffect(() => {

    document.getElementById('item').value=item;
  },[]);

  return (        
    <main className='container top1'>

        <div className='row'>
              <Link to={'/stock-category/'+category+'/'+subcategory} className='col-12'><i class="fas fa-caret-left"></i></Link>              
        </div>

        <div className='row top1'> 
            <div className='col-12 green'><b> {item} </b><span className='lightgrey'> |settings</span>           
            </div>            
        </div>
         {index}   
        <form className='row'>
            <label className='col-12 top1 grey'>Item Name  {newItemName}               
                <input className='col-12' name='item' id='item'  onKeyUp={()=>{
                    setNewItemNamef();    
                }}></input>

                {   newItemName!=item &&
                    <button className='left col-1 top1 green fas fa-undo-alt' type='button' onClick={()=>{
                        setNewItemName(item)
                        document.getElementById('item').value=item;
                        }}></button>
                }


            </label>
        </form>

        <div className='row top1'>
            { index>1 && <button className='col-1 border'><i class="fas fa-angle-left" onClick={()=>setIndexf(-1)}></i></button>}
            { newItemName!='' &&  <button className='col-1 left2 border' onClick={()=>setIndexf(1)}><i class="fas fa-angle-right"></i></button>}
        </div>

    </main>            
  );
}

export default ItemSettings;
