  const [username, setUsername] = useState('');  
  const [email, setEmail] = useState('');  


  useEffect(()=>{

    // let initials=localStorage.getItem('username');
    //     initials=initials.split('');
    //     initials=initials[0];
    setUsername(localStorage.getItem('username'));


  },[]);

  return (        
    <header className='container top2  maincontent'>
      {
        username=='' &&
        <div className='container'><img src={logo} className='button' id='logo'></img><span className='button'> <b>Estia</b></span></div>
      }

      {
        username!='' &&
        <div className='container'><img src={logo} className='button' id='logo'></img><span className='lightgrey button'> |{username}</span></div>
      }


    </header>            
  );
}

export default Header;
