import './transactions.css';


import React from "react";


function Transactions() {

 let items=[{item:'orangesorangesorangesoranges',balance:2,total:8,minimum:5},
            {item:'apples',balance:-1,total:5,minimum:6},
            {item:'bananas',balance:-1,total:8,minimum:7},  
            {item:'apples',balance:-1,total:5,minimum:6},
            {item:'bananas',balance:-1,total:8,minimum:7},  
            {item:'apples',balance:-1,total:5,minimum:6},
            {item:'bananas',balance:-1,total:8,minimum:7},  
            {item:'apples',balance:-1,total:5,minimum:6},
            {item:'bananas',balance:-1,total:8,minimum:7},  
            {item:'apples',balance:-1,total:5,minimum:6},
            {item:'bananas',balance:-1,total:8,minimum:7},  
            {item:'apples',balance:-1,total:5,minimum:6},
            {item:'bananas',balance:-1,total:8,minimum:7},  
           ];


  function getItems(items){

    let itemsList=[];
    let l=items.length;
    let c;

    for(c=0;c<l;c++){


      if(items[c].balance<0){
        itemsList.push( 
          <div className='row top1 record' key={c}>         
          <p className='col-2 margin-v-auto left vertical-centre'>{items[c].item}</p>
          <p className='col-2 margin-v-auto left vertical-centre negative'>{items[c].balance}</p>
          <p className='col-2 margin-v-auto left vertical-centre'>{items[c].total}</p>
          <p className='col-2 margin-v-auto left vertical-centre'>{items[c].minimum}</p>
          <button className='col'><i class="fas fa-caret-right"></i></button>
        </div>
        );
      }
      else{
        itemsList.push(
          <div className='row top1 record' key={c}>         
          <p className='col-12 margin-v-auto left vertical-centre'>{items[c].item}</p>
          <p className='col-2 margin-v-auto left vertical-centre positive'>{items[c].balance}</p>
          <p className='col-2 margin-v-auto left vertical-centre'>{items[c].total}</p>
          <p className='col-2 margin-v-auto left vertical-centre'>{items[c].minimum}</p>
          <button className='col'><i class="fas fa-caret-right"></i></button>
        </div>
        );
      }



    }


    return(
      <div>
        {itemsList}
      </div>
    );

  }





  return (
    <div className="container">

      <main className='container'>
          <div className='row left'>
              <h3 className='col'>Recent transactions</h3>
          </div>

          <div className='row top2 headers sticky-top'>
              <h5 className='col left'>Item</h5>
              <h5 className='col left'>+ / -</h5>
              <h5 className='col left'>Total</h5>
              <h5 className='col left'>Minimum</h5>
              <h5 className='col left'></h5>
          </div>
          <div>
            {getItems(items)}
          </div>
          {/* <div className='row record'> 
            <p className='col margin-v-auto left vertical-centre'>1</p>
            <p className='col margin-v-auto left vertical-centre'>+2</p>
            <p className='col margin-v-auto left vertical-centre'>8</p>
            <p className='col margin-v-auto left vertical-centre'>5</p>
            <button className='col'><i class="fas fa-caret-right"></i></button>
          </div> */}


      </main>




    </div>
  );
}

export default Transactions;
