// import './main.css';


import React from "react";

import groceries from './images/stock/groceries1.png';
import household from './images/stock/household.png'
import  { useState,useEffect } from 'react';

function Stock() {

    const [stockSelected, select] = useState(0);

    useEffect(() => {
         let menuItems=document.getElementsByClassName('button-img');
         let l=menuItems.length;   
         let c;
         for(c=0;c<l;c++){
             menuItems[c].style.color='black';
         }

         menuItems[stockSelected].style.color='darkcyan';
      });


  return (        
    <main className='container top2'>
        <div className='row left'>
            <h5 className='col'>Stock</h5>
        </div>


              <button className='button-img' onClick={()=>select(0)}>      
              <i className="fas fa-lemon "></i><br></br>
                <small>Groceries</small>
              </button>

              <button className=' left2 button-img' onClick={()=>select(1)}>
              <i className="fas fa-soap "></i><br></br>
                    <small>Household</small>
              </button>




    </main>            
  );
}

export default Stock;
